import React, { useState, useEffect } from 'react';
import { ChevronDown, Shield, Users, FileText, CheckCircle, AlertTriangle, Download, Info, Calculator, TrendingUp, Calendar, BookOpen } from 'lucide-react';

const AIDecisionTool = () => {
  const [selectedIndustry, setSelectedIndustry] = useState('');
  const [selectedUseCase, setSelectedUseCase] = useState('');
  const [selectedTool, setSelectedTool] = useState(null);
  const [showResults, setShowResults] = useState(false);
  const [activeTab, setActiveTab] = useState('recommendation');
  const [orgSize, setOrgSize] = useState('');
  const [currentMaturity, setCurrentMaturity] = useState('');

  const industries = [
    'Healthcare & Public Health',
    'Education & EdTech', 
    'Financial Services',
    'Technology & Software',
    'Media & Entertainment',
    'Professional Services',
    'Manufacturing & Advanced Industries',
    'Retail & Consumer Goods',
    'Energy & Materials',
    'Aerospace & Defense',
    'Automotive & Assembly',
    'Telecommunications',
    'Real Estate',
    'Agriculture & Food Security',
    'Pharmaceuticals & Life Sciences',
    'Legal Services',
    'Human Rights & Advocacy',
    'Environmental & Conservation',
    'Social Services',
    'Community Development',
    'Arts & Culture',
    'International Development',
    'Religious / Faith-based Organisations',
    'Microfinance & Social Enterprise',
    'Public Sector & Government',
    'Research Institutes & Think Tanks'
  ];

  const useCases = [
    'Grant writing & funding proposals',
    'Board reports & governance documents',
    'Donor communications & messaging',
    'Program evaluation & analysis',
    'Policy drafts & position papers',
    'Email & correspondence management',
    'Meeting summaries & action items',
    'Data analysis & reporting',
    'Content creation & marketing',
    'Training materials & education',
    'Research synthesis & insights',
    'Strategic planning documents',
    'Legal document review',
    'Financial analysis & budgeting',
    'HR policies & procedures',
    'Client communications',
    'Presentation development',
    'Social media management',
    'Website content & SEO',
    'Competitive analysis'
  ];

  const orgSizes = [
    { value: 'startup', label: 'Startup (1-10 employees)', multiplier: 0.8 },
    { value: 'small', label: 'Small (11-50 employees)', multiplier: 1.0 },
    { value: 'medium', label: 'Medium (51-200 employees)', multiplier: 1.2 },
    { value: 'large', label: 'Large (201-1000 employees)', multiplier: 1.5 },
    { value: 'enterprise', label: 'Enterprise (1000+ employees)', multiplier: 2.0 }
  ];

  const maturityLevels = [
    { value: 'none', label: 'No AI experience', risk: 0.3 },
    { value: 'basic', label: 'Basic AI tool usage', risk: 0.2 },
    { value: 'intermediate', label: 'Some AI integration', risk: 0.1 },
    { value: 'advanced', label: 'Systematic AI adoption', risk: 0.05 }
  ];

  const aiTools = {
    'ChatGPT': {
      tiers: {
        'Free': { price: 0, risk: 'High', suitable: false, features: ['GPT-4o mini', 'Limited usage', 'Basic web browsing'] },
        'Plus': { price: 20, risk: 'Moderate-High', suitable: false, features: ['GPT-4o access', 'Priority speed', 'DALL-E 3', 'Custom GPTs'] },
        'Team': { price: 25, risk: 'Low', suitable: true, features: ['Admin controls', 'SOC 2 compliance', 'No training on data', 'Team collaboration'] },
        'Enterprise': { price: 60, risk: 'Very Low', suitable: true, features: ['Full compliance', 'SSO integration', 'Advanced admin', 'Custom models'] }
      },
      strengths: ['Versatile content generation', 'Strong reasoning', 'Multimodal capabilities', 'Plugin ecosystem'],
      bestFor: ['Grant writing', 'Creative content', 'General correspondence', 'Brainstorming'],
      dataPolicy: 'Free/Plus: May use data for training unless opted out. Team/Enterprise: Data not used for training by default.',
      productivityGain: 0.35,
      implementationTime: 2
    },
    'Claude': {
      tiers: {
        'Free': { price: 0, risk: 'Low-Moderate', suitable: true, features: ['Basic Claude access', 'Limited usage', 'Privacy-first design'] },
        'Pro': { price: 20, risk: 'Low', suitable: true, features: ['Extended context', 'Priority access', 'Advanced features'] },
        'Team': { price: 25, risk: 'Very Low', suitable: true, features: ['Team collaboration', 'Admin controls', 'Enhanced security'] },
        'Enterprise': { price: 60, risk: 'Very Low', suitable: true, features: ['Full compliance', 'Advanced security', 'Custom deployment'] }
      },
      strengths: ['Strong safety features', 'Excellent reasoning', 'Large document handling', 'Nuanced responses'],
      bestFor: ['Sensitive communications', 'Policy analysis', 'Long-form content', 'Risk-averse environments'],
      dataPolicy: 'Does not use user data for training across all tiers unless explicit consent given.',
      productivityGain: 0.30,
      implementationTime: 1
    },
    'Google Gemini': {
      tiers: {
        'Free': { price: 0, risk: 'Moderate', suitable: false, features: ['Basic Gemini access', 'Google integration', 'Limited usage'] },
        'Pro': { price: 20, risk: 'Low-Moderate', suitable: true, features: ['Gemini 2.5 Pro', '2TB storage', 'Advanced features'] },
        'Workspace Enterprise': { price: 30, risk: 'Low', suitable: true, features: ['Full Google Workspace', 'Enterprise security', 'Admin controls'] }
      },
      strengths: ['Google Workspace integration', 'Multimodal capabilities', 'Large context windows', 'Real-time collaboration'],
      bestFor: ['Document collaboration', 'Email management', 'Meeting summaries', 'Google ecosystem users'],
      dataPolicy: 'Business plans include enterprise privacy. Free tier follows standard Google policies.',
      productivityGain: 0.25,
      implementationTime: 3
    },
    'Microsoft Copilot': {
      tiers: {
        'Business': { price: 30, risk: 'Low', suitable: true, features: ['Full M365 integration', 'Enterprise security', 'Admin controls'] }
      },
      strengths: ['Deep M365 integration', 'Familiar interface', 'Enterprise security', 'Seamless workflow'],
      bestFor: ['Email drafting', 'Document creation', 'Data analysis', 'Presentation building'],
      dataPolicy: 'Enterprise-grade privacy with M365 security framework. Data not used for training.',
      productivityGain: 0.28,
      implementationTime: 4
    }
  };

  const getRecommendation = (industry, useCase) => {
    const recommendations = {
      'Healthcare & Public Health': {
        'Grant writing & funding proposals': ['Claude', 'ChatGPT'],
        'Policy drafts & position papers': ['Claude', 'ChatGPT'],
        'Data analysis & reporting': ['Microsoft Copilot', 'ChatGPT'],
        'Training materials & education': ['ChatGPT', 'Google Gemini'],
        'default': ['Claude', 'Microsoft Copilot']
      },
      'Financial Services': {
        'Data analysis & reporting': ['Microsoft Copilot', 'ChatGPT'],
        'Legal document review': ['Claude', 'ChatGPT'],
        'Client communications': ['Google Gemini', 'Microsoft Copilot'],
        'Competitive analysis': ['ChatGPT', 'Claude'],
        'default': ['Microsoft Copilot', 'Claude']
      },
      'Technology & Software': {
        'Content creation & marketing': ['ChatGPT', 'Google Gemini'],
        'Data analysis & reporting': ['ChatGPT', 'Microsoft Copilot'],
        'Research synthesis & insights': ['ChatGPT', 'Claude'],
        'Competitive analysis': ['ChatGPT', 'Google Gemini'],
        'default': ['ChatGPT', 'Google Gemini']
      },
      'Education & EdTech': {
        'Training materials & education': ['ChatGPT', 'Google Gemini'],
        'Grant writing & funding proposals': ['Claude', 'ChatGPT'],
        'Content creation & marketing': ['ChatGPT', 'Google Gemini'],
        'Research synthesis & insights': ['Claude', 'ChatGPT'],
        'default': ['ChatGPT', 'Google Gemini']
      },
      'Legal Services': {
        'Legal document review': ['Claude', 'ChatGPT'],
        'Policy drafts & position papers': ['Claude', 'ChatGPT'],
        'Research synthesis & insights': ['Claude', 'ChatGPT'],
        'Client communications': ['Claude', 'Microsoft Copilot'],
        'default': ['Claude', 'ChatGPT']
      },
      'default': {
        'Email & correspondence management': ['Microsoft Copilot', 'Google Gemini'],
        'Data analysis & reporting': ['Microsoft Copilot', 'ChatGPT'],
        'Content creation & marketing': ['ChatGPT', 'Google Gemini'],
        'Meeting summaries & action items': ['Microsoft Copilot', 'Google Gemini'],
        'Grant writing & funding proposals': ['Claude', 'ChatGPT'],
        'Training materials & education': ['ChatGPT', 'Google Gemini'],
        'Research synthesis & insights': ['Claude', 'ChatGPT'],
        'default': ['ChatGPT', 'Claude']
      }
    };

    const industryRecs = recommendations[industry] || recommendations['default'];
    return industryRecs[useCase] || industryRecs['default'];
  };

  const calculateROI = () => {
    if (!selectedTool || !orgSize) return null;

    const orgData = orgSizes.find(size => size.value === orgSize);
    const tool = aiTools[selectedTool];
    const bestTier = Object.entries(tool.tiers).find(([_, details]) => details.suitable);

    if (!bestTier || !orgData) return null;

    // Calculate realistic user estimates based on org size
    let usersEstimate;
    switch (orgSize) {
      case 'startup': usersEstimate = Math.min(5, 10); // 50% of startup employees
        break;
      case 'small': usersEstimate = Math.min(Math.round(30 * 0.6), 50); // 60% of small org
        break;
      case 'medium': usersEstimate = Math.min(Math.round(125 * 0.4), 125); // 40% of medium org
        break;
      case 'large': usersEstimate = Math.min(Math.round(600 * 0.3), 600); // 30% of large org
        break;
      case 'enterprise': usersEstimate = Math.min(Math.round(2000 * 0.2), 2000); // 20% of enterprise
        break;
      default: usersEstimate = 10;
    }

    const monthlyPrice = bestTier[1].price;
    const annualSoftwareCost = monthlyPrice * usersEstimate * 12;

    // Training and implementation costs scaled appropriately
    const trainingCostPerUser = orgSize === 'startup' ? 100 : orgSize === 'small' ? 125 : 150;
    const trainingCost = usersEstimate * trainingCostPerUser;

    // Implementation cost based on org complexity
    let implementationCost;
    switch (orgSize) {
      case 'startup': implementationCost = 2500;
        break;
      case 'small': implementationCost = 7500;
        break;
      case 'medium': implementationCost = 15000;
        break;
      case 'large': implementationCost = 35000;
        break;
      case 'enterprise': implementationCost = 75000;
        break;
      default: implementationCost = 5000;
    }

    const totalAnnualCost = annualSoftwareCost + trainingCost + implementationCost;

    // Benefits calculation adjusted for org size
    const avgHourlyWage = orgSize === 'startup' ? 35 : orgSize === 'small' ? 45 : orgSize === 'medium' ? 55 : orgSize === 'large' ? 65 : 75;
    const hoursPerWeek = 4; // Time saved per user per week
    const weeksPerYear = 50;
    const productivityGain = tool.productivityGain;

    const timeSavings = usersEstimate * hoursPerWeek * weeksPerYear * avgHourlyWage * productivityGain;
    const qualityImprovement = timeSavings * 0.3; // 30% additional value from quality
    const totalAnnualBenefit = timeSavings + qualityImprovement;

    const netBenefit = totalAnnualBenefit - totalAnnualCost;
    const roi = (netBenefit / totalAnnualCost) * 100;
    const paybackMonths = totalAnnualCost / (totalAnnualBenefit / 12);

    return {
      users: Math.round(usersEstimate),
      annualCost: Math.round(totalAnnualCost),
      annualBenefit: Math.round(totalAnnualBenefit),
      netBenefit: Math.round(netBenefit),
      roi: Math.round(roi),
      paybackMonths: Math.round(paybackMonths * 10) / 10,
      softwareCost: Math.round(annualSoftwareCost),
      trainingCost: Math.round(trainingCost),
      implementationCost: Math.round(implementationCost)
    };
  };

  const generateRoadmap = () => {
    const maturityLevel = maturityLevels.find(level => level.value === currentMaturity);
    const baseTimeline = aiTools[selectedTool]?.implementationTime || 2;
    const adjustedTimeline = baseTimeline + (maturityLevel?.risk * 4 || 0);

    return {
      phase1: {
        name: "Foundation Setup",
        duration: `${Math.round(adjustedTimeline)} weeks`,
        tasks: [
          "Executive alignment and governance setup",
          "Technical infrastructure preparation", 
          "Policy development and approval",
          "Pilot group selection and preparation"
        ]
      },
      phase2: {
        name: "Pilot Execution", 
        duration: "4-6 weeks",
        tasks: [
          "Executive and manager training delivery",
          "Pilot user onboarding and tool deployment",
          "Workflow integration and support",
          "Performance monitoring and feedback collection"
        ]
      },
      phase3: {
        name: "Scale and Optimise",
        duration: "2-4 weeks", 
        tasks: [
          "Pilot evaluation and lessons learned",
          "Scale preparation and process optimisation",
          "Change management enhancement",
          "Success measurement and reporting"
        ]
      }
    };
  };

  const handleAnalyse = () => {
    if (selectedIndustry && selectedUseCase) {
      const recs = getRecommendation(selectedIndustry, selectedUseCase);
      setSelectedTool(recs[0]);
      setShowResults(true);
    }
  };

  const getRiskColor = (risk) => {
    switch (risk) {
      case 'Very Low': return 'text-green-600 bg-green-50';
      case 'Low': return 'text-green-500 bg-green-50';
      case 'Low-Moderate': return 'text-yellow-600 bg-yellow-50';
      case 'Moderate': return 'text-orange-500 bg-orange-50';
      case 'Moderate-High': return 'text-red-500 bg-red-50';
      case 'High': return 'text-red-600 bg-red-50';
      default: return 'text-gray-500 bg-gray-50';
    }
  };

  const getSuitabilityIcon = (suitable) => {
    return suitable ? 
      <CheckCircle className="w-4 h-4 text-green-500" /> : 
      <AlertTriangle className="w-4 h-4 text-red-500" />;
  };

  const comprehensiveTrainingCurriculum = `
# Complete AI Leadership Training Curriculum
## Based on McKinsey Superagency Research & The 1% Success Framework

---

## 🎯 EXECUTIVE OVERVIEW
Transform from AI-curious to AI-confident leader in 5 comprehensive modules. This curriculum is designed for executives who want to join the 1% of organisations achieving AI maturity.

**Total Duration:** 15 hours (3 hours per module)
**Format:** Executive workshops + practical application
**Outcome:** Complete AI leadership capability and 90-day implementation plan

---

## 📚 MODULE 1: AI FUNDAMENTALS FOR EXECUTIVES (3 hours)
### Learning Objectives:
✅ Understand AI vs traditional software and key terminology
✅ Assess AI's business impact across 15 disrupted industries  
✅ Evaluate current market landscape and competitive positioning
✅ Develop AI strategic vision aligned with business objectives

### Session Structure:
**Hour 1: AI Technology Landscape**
- The AI revolution: More than automation
- Understanding large language models and their capabilities
- AI vs machine learning vs automation: Key distinctions
- Current limitations and realistic expectations

**Hour 2: Business Impact Analysis**
- Industry-specific AI disruption patterns
- ${selectedIndustry} AI opportunities and threats
- Competitive intelligence and market positioning
- ROI frameworks and success stories

**Hour 3: Strategic Planning Workshop**
- AI readiness assessment for your organisation
- Identifying high-impact use cases for ${selectedUseCase}
- Resource requirements and investment planning
- Creating executive-level AI vision statement

### Deliverables:
□ AI opportunity assessment for your industry
□ Prioritised use case matrix
□ Executive AI vision statement
□ Resource allocation framework

---

## 🔒 MODULE 2: PRIVACY & SECURITY LEADERSHIP (3 hours)
### Learning Objectives:
✅ Master data classification and risk management frameworks
✅ Navigate GDPR, Privacy Act, and sector-specific compliance
✅ Distinguish enterprise vs consumer AI tool capabilities
✅ Implement data governance and retention policies

### Session Structure:
**Hour 1: Data Risk Framework**
- Data classification: Public, Internal, Confidential, Restricted
- AI-specific privacy risks and mitigation strategies
- Third-party AI vendor risk assessment
- Incident response and breach management

**Hour 2: Regulatory Compliance**
- GDPR Article 22 and automated decision-making
- Australian Privacy Act implications for AI
- ${selectedIndustry}-specific regulatory requirements
- Global compliance strategy for multi-jurisdiction operations

**Hour 3: Enterprise AI Security**
- Enterprise vs consumer AI tool comparison
- SSO, MFA, and access control implementation
- Data residency and sovereignty considerations
- Vendor security assessment frameworks

### Deliverables:
□ Organisation data classification system
□ AI vendor security assessment checklist
□ Compliance risk register and mitigation plan
□ Privacy by design implementation guide

---

## ⚡ MODULE 3: STRATEGIC TOOL SELECTION (3 hours)
### Learning Objectives:
✅ Evaluate AI tools using business needs assessment framework
✅ Conduct comprehensive risk vs benefit analysis
✅ Master cost-benefit evaluation and procurement strategies
✅ Design phased implementation and scaling plans

### Session Structure:
**Hour 1: Business Needs Assessment**
- Mapping AI tools to business processes
- ${selectedUseCase} specific tool requirements
- User persona development and access planning
- Integration requirements with existing systems

**Hour 2: Evaluation Framework**
- Risk vs benefit scoring methodology
- Total cost of ownership calculation
- Vendor evaluation criteria and scoring
- Pilot program design and success metrics

**Hour 3: Procurement Strategy**
- Contract negotiation for AI tools
- SLA requirements and performance metrics
- Scaling economics and volume discounts
- Multi-vendor strategy vs single-platform approach

### Deliverables:
□ AI tool evaluation matrix
□ Business case for ${selectedTool} implementation
□ Procurement strategy and vendor negotiation plan
□ Pilot program design and success criteria

---

## 🏛️ MODULE 4: GOVERNANCE & OVERSIGHT (3 hours)
### Learning Objectives:
✅ Design AI steering committee and governance structure
✅ Develop comprehensive AI usage policies and procedures
✅ Implement user access management and monitoring systems
✅ Create incident response and continuous review processes

### Session Structure:
**Hour 1: Governance Structure Design**
- AI steering committee formation and charter
- Roles, responsibilities, and decision-making authority
- Meeting cadence and escalation procedures
- Executive sponsorship and accountability framework

**Hour 2: Policy Development Workshop**
- AI usage policy template customisation
- User access management framework
- Training requirements and certification processes
- Compliance monitoring and audit procedures

**Hour 3: Risk Management Systems**
- Incident response playbook development
- Monitoring dashboards and alert systems
- Regular review cycles and improvement processes
- Vendor management and relationship oversight

### Deliverables:
□ AI governance charter and committee structure
□ Comprehensive AI usage policies
□ User access management system
□ Incident response and monitoring framework

---

## 🚀 MODULE 5: PRACTICAL IMPLEMENTATION (3 hours)
### Learning Objectives:
✅ Execute 90-day AI transformation roadmap
✅ Design and deliver staff training programs across all levels
✅ Master change management using ADKAR-AI methodology
✅ Implement success metrics and continuous improvement systems

### Session Structure:
**Hour 1: Implementation Roadmap**
- 90-day phased implementation plan
- Week-by-week milestone and decision gates
- Resource allocation and team coordination
- Risk mitigation and contingency planning

**Hour 2: Change Management Mastery**
- ADKAR-AI model for AI adoption
- Resistance pattern identification and response
- Communication strategy and stakeholder engagement
- Success celebration and recognition programs

**Hour 3: Success Measurement**
- AI Success Scorecard implementation
- ROI tracking and business case validation
- Continuous improvement engine setup
- Scaling strategy and competitive advantage

### Deliverables:
□ Complete 90-day implementation roadmap
□ Staff training program for all levels
□ Change management strategy and communication plan
□ Success measurement dashboard and review processes

---

## 🏆 CERTIFICATION REQUIREMENTS
### Assessment Criteria:
✅ Complete all 5 modules with 90% attendance
✅ Pass comprehensive assessment (80% minimum score)
✅ Develop organisation-specific AI implementation plan
✅ Present business case and strategy to leadership team
✅ Demonstrate practical application through pilot project

### Certification Levels:
**🥉 AI Leadership Foundations** - Modules 1-2 complete
**🥈 AI Strategic Planner** - Modules 1-3 complete  
**🥇 AI Transformation Leader** - All modules + successful pilot
**💎 AI Maturity Champion** - Full certification + organisational transformation

---

## 📊 SUCCESS METRICS
### Individual Leader Assessment:
- **Knowledge Retention:** 85%+ on post-training assessments
- **Implementation Success:** Successful pilot completion within 90 days
- **Team Adoption:** 80%+ user adoption in pilot groups
- **Business Impact:** Measurable ROI demonstration

### Organisational Transformation:
- **Governance Maturity:** Complete framework implementation
- **Risk Management:** Zero significant incidents in first year
- **Competitive Advantage:** Market recognition as AI leader
- **Financial Performance:** 15-30% operational efficiency improvement

---

## 🔗 INTEGRATION WITH AI DECISION TOOL
This curriculum is designed to work seamlessly with your AI Decision Support Tool:

✅ **Pre-Training:** Use tool for initial assessment and baseline
✅ **During Training:** Apply learnings to refine tool recommendations  
✅ **Post-Training:** Use tool for ongoing decision support and team guidance
✅ **Continuous Improvement:** Tool becomes your AI governance dashboard

---

## 📚 COMPLETE RESOURCE LIBRARY
### Templates & Frameworks:
- AI Strategic Charter Template
- Governance Policy Templates  
- Risk Assessment Matrices
- ROI Calculation Worksheets
- Training Program Curricula
- Change Management Playbooks

### Assessment Tools:
- AI Readiness Assessment
- Success Scorecard Framework
- Vendor Evaluation Criteria
- Training Effectiveness Measures

### Implementation Guides:
- 90-Day Roadmap Template
- Communication Strategy Scripts
- Best Practice Case Studies  
- Troubleshooting Guides

---

**READY TO JOIN THE 1% OF AI-MATURE ORGANISATIONS?**

*Contact us to customise this curriculum for your organisation and industry.*
`;

  const policyTemplate = `
# AI Usage Policy Template
## Customised for ${selectedIndustry}

## Purpose
This policy establishes guidelines for the responsible use of artificial intelligence tools within [Organisation Name] to ensure data security, privacy compliance, and operational effectiveness.

## Scope
This policy applies to all staff, contractors, and volunteers who use AI tools for organisational purposes.

## Approved AI Tools & Tiers
Based on our risk assessment, the following tools are approved:

### Tier 1 - Sensitive Data Approved
- **Tool**: ${selectedTool || '[Selected Tool]'}
- **Tier**: Enterprise/Team
- **Use Cases**: Donor data, legal documents, strategic planning
- **Data Classification**: Confidential and above

### Tier 2 - General Business Use
- **Tools**: [List secondary tools]
- **Use Cases**: General correspondence, marketing content
- **Data Classification**: Internal use only

## Data Handling Requirements
1. **Confidential Data**: Must use Tier 1 tools only
2. **Personal Information**: Requires data minimisation and pseudonymisation
3. **Public Information**: May use any approved tool
4. **Retention**: Follow organisational data retention policies

## Security Requirements
- All users must complete AI security training
- Regular security assessments required
- Incident reporting procedures must be followed
- Admin oversight for enterprise tools required

## Compliance Considerations
- GDPR compliance for EU operations
- Privacy Act compliance for Australian operations
- ${selectedIndustry}-specific regulations
- Regular policy reviews and updates

## Training Requirements
- Initial AI literacy training (mandatory)
- Security awareness training (quarterly)
- Tool-specific training for approved platforms
- Ongoing professional development

## Implementation Timeline
- Policy approval: Week 1
- Tool procurement: Week 2-3
- Training rollout: Week 4-6
- Full deployment: Week 8-10

This policy will be reviewed annually or as AI technology evolves.
`;

  const roi = calculateROI();
  const roadmap = selectedTool ? generateRoadmap() : null;

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-4">
      <div className="max-w-6xl mx-auto">
        {/* Header */}
        <div className="text-center mb-8">
          <div className="inline-flex items-center gap-3 mb-4">
            <div className="w-12 h-12 bg-gradient-to-r from-blue-600 to-indigo-600 rounded-xl flex items-center justify-center">
              <Shield className="w-6 h-6 text-white" />
            </div>
            <div>
              <h1 className="text-3xl font-bold text-gray-900">AI Decision Support Tool</h1>
              <p className="text-lg text-gray-600">For Privacy-Conscious Executives</p>
            </div>
          </div>
          <p className="text-xl text-gray-700 max-w-3xl mx-auto">
            Navigate AI tool selection with confidence. Get personalised recommendations 
            that prioritise data security and regulatory compliance for your industry.
          </p>
        </div>

        {/* Enhanced Input Section */}
        <div className="bg-white rounded-2xl shadow-xl p-8 mb-8">
          <h2 className="text-2xl font-semibold text-gray-900 mb-6 flex items-center gap-2">
            <Users className="w-6 h-6 text-blue-600" />
            Tell Us About Your Organisation
          </h2>

          <div className="grid md:grid-cols-2 gap-6 mb-6">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Industry Sector
              </label>
              <div className="relative">
                <select
                  value={selectedIndustry}
                  onChange={(e) => setSelectedIndustry(e.target.value)}
                  className="w-full p-4 pr-10 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent appearance-none bg-white"
                >
                  <option value="">Select your industry...</option>
                  {industries.map(industry => (
                    <option key={industry} value={industry}>{industry}</option>
                  ))}
                </select>
                <ChevronDown className="absolute right-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400 pointer-events-none" />
              </div>
            </div>

            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Primary Use Case
              </label>
              <div className="relative">
                <select
                  value={selectedUseCase}
                  onChange={(e) => setSelectedUseCase(e.target.value)}
                  className="w-full p-4 pr-10 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent appearance-none bg-white"
                >
                  <option value="">Select your primary use case...</option>
                  {useCases.map(useCase => (
                    <option key={useCase} value={useCase}>{useCase}</option>
                  ))}
                </select>
                <ChevronDown className="absolute right-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400 pointer-events-none" />
              </div>
            </div>
          </div>

          <div className="grid md:grid-cols-2 gap-6 mb-6">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">
                Organisation Size
              </label>
              <div className="relative">
                <select
                  value={orgSize}
                  onChange={(e) => setOrgSize(e.target.value)}
                  className="w-full p-4 pr-10 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent appearance-none bg-white"
                >
                  <option value="">Select organisation size...</option>
                  {orgSizes.map(size => (
                    <option key={size.value} value={size.value}>{size.label}</option>
                  ))}
                </select>
                <ChevronDown className="absolute right-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400 pointer-events-none" />
