  targetFolder.addFile(file);
  DriveApp.getRootFolder().removeFile(file);

  // CLEAN DOCUMENT STRUCTURE - No meta-commentary about modifications
  if (srcPack.pdfFiles && srcPack.pdfFiles.length > 0) {
    const pdfNames = srcPack.pdfFiles.map(pdf => pdf.name).join(', ');
    doc.getBody().appendParagraph(`Source Materials: ${pdfNames}`).setHeading(DocumentApp.ParagraphHeading.HEADING3);
    doc.getBody().appendParagraph('');
  }

  // Add the main content without modification references
  doc.getBody().appendParagraph(rec);
  doc.saveAndClose();

  // Update the mapping sheet
  sh.getRange(r,4).setValue(doc.getUrl());

  // Extract and populate module names (Columns G-S)
  try {
    Logger.log('Starting module extraction...');
    
    const moduleNames = extractModuleNames_(rec);
    
    if (moduleNames && moduleNames.length > 0) {
      // Column G: Module List (all module names)
      sh.getRange(r, 7).setValue(moduleNames.join('\n'));
      
      // Columns H-S: Individual module names (up to 12 modules)
      for (let i = 0; i < 12; i++) {
        const moduleName = moduleNames[i] || '';
        sh.getRange(r, 8 + i).setValue(moduleName);
      }
      
      Logger.log(`Successfully extracted ${moduleNames.length} modules for concept: ${concept}`);
      
      // FIXED: Generate LMS content automatically after successful module extraction
      try {
        Logger.log('Generating LMS content...');
        
        // Check if generateLMSContent exists and call it
        if (typeof generateLMSContent_ === 'function') {
          generateLMSContent_(r);
          Logger.log('LMS content generated successfully');
        } else {
          Logger.log('generateLMSContent_ function not found - LMS content not generated');
        }
        
      } catch (lmsError) {
        Logger.log('Error generating LMS content: ' + lmsError.toString());
      }
      
    } else {
      Logger.log('No modules found in recommendation text - check RECOMMENDED MODULES section format');
      
      // Try alternative extraction if standard format fails
      const alternativeModules = rec.match(/Module \d+[:\-\s]+([^\n]+)/gi);
      if (alternativeModules) {
        const cleanModules = alternativeModules.map(m => m.replace(/Module \d+[:\-\s]+/i, '').trim());
        sh.getRange(r, 7).setValue(cleanModules.join('\n'));
        Logger.log(`Alternative extraction found ${cleanModules.length} modules`);
      }
    }
    
  } catch (moduleError) {
    Logger.log('Error extracting modules: ' + moduleError.toString());
  }

  // Success notification with PDF confirmation
  let successMessage = `Course recommendation generated successfully!\n\nDocument: ${docName}`;
  if (srcPack.pdfFiles && srcPack.pdfFiles.length > 0) {
    const pdfNames = srcPack.pdfFiles.map(pdf => pdf.name).join(', ');
    successMessage += `\n\nPDF documents integrated: ${pdfNames}`;
  }
  
  ui.alert('Success', successMessage, ui.ButtonSet.OK);
}

/**
 * Generate LMS Content for a single module from Module-Resources tab
 * @param {number} row - The row number from the Mapping sheet
 */
function generateLMSContent_(row) {
  try {
    const sh = SpreadsheetApp.getActiveSheet();
    const concept = sh.getRange(row, 1).getValue();
    const audience = sh.getRange(row, 3).getValue() || 'Clinical';
    const courseFolderUrl = sh.getRange(row, 20).getValue();
    
    if (!concept || !courseFolderUrl) {
      Logger.log('Missing concept or course folder URL for LMS content generation');
      return;
    }
    
    // Get the Module-Resources sheet for this concept
    const ss = SpreadsheetApp.getActive();
    const moduleResourcesSheet = ss.getSheetByName(`Module-Resources-${concept}`);
    
    if (!moduleResourcesSheet) {
      Logger.log(`No Module-Resources sheet found for concept: ${concept}`);
      return;
    }
    
    // Find the current module being processed
    // Look for the active module in the mapping sheet columns H-S
    let currentModule = '';
    let moduleFound = false;
    
    // Check columns H-S (8-19) to find which module we're working with
    for (let col = 8; col <= 19; col++) {
      const moduleName = sh.getRange(row, col).getValue();
      if (moduleName && String(moduleName).trim()) {
        // Check if this module exists in Module-Resources sheet
        const lastRow = moduleResourcesSheet.getLastRow();
        for (let r = 2; r <= lastRow; r++) {
          const sheetModuleName = String(moduleResourcesSheet.getRange(r, 1).getValue()).trim();
          if (sheetModuleName === String(moduleName).trim()) {
            currentModule = String(moduleName).trim();
            moduleFound = true;
            
            // Get the content from Module-Resources sheet
            const moduleDescription = moduleResourcesSheet.getRange(r, 3).getValue(); // Column C
            const keyConcepts = moduleResourcesSheet.getRange(r, 4).getValue(); // Column D  
            const slideSpecifications = moduleResourcesSheet.getRange(r, 8).getValue(); // Column H
            
            // Create LMS document
            createLMSDocument(concept, currentModule, moduleDescription, keyConcepts, slideSpecifications, courseFolderUrl, row, col);
            
            Logger.log(`Generated LMS content for module: ${currentModule}`);
            return; // Exit after processing first found module
          }
        }
      }
    }
    
    if (!moduleFound) {
      Logger.log('No matching module found in Module-Resources sheet');
    }
    
  } catch (error) {
    Logger.log('Error in generateLMSContent_: ' + error.toString());
  }
}

/**
 * Create LMS document with D + C + H content from Module-Resources tab
 * @param {string} concept - Course concept name
 * @param {string} moduleName - Module name
 * @param {string} moduleDescription - Column C content
 * @param {string} keyConcepts - Column D content
 * @param {string} slideSpecifications - Column H content
 * @param {string} courseFolderUrl - Course folder URL
 * @param {number} mappingRow - Mapping sheet row
 * @param {number} mappingCol - Mapping sheet column for this module
 */
function createLMSDocument(concept, moduleName, moduleDescription, keyConcepts, slideSpecifications, courseFolderUrl, mappingRow, mappingCol) {
  try {
    // Create LMS document
    const timestamp = new Date().toISOString().slice(0, 16).replace('T', '_');
    const docName = `${concept}_${moduleName}_LMS_${timestamp}`;
    const doc = DocumentApp.create(docName);
    
    // Add to course folder
    let file = DriveApp.getFileById(doc.getId());
    const targetFolder = DriveApp.getFolderById(courseFolderUrl.split('/folders/')[1]);
    targetFolder.addFile(file);
    DriveApp.getRootFolder().removeFile(file);
    
    // Document structure: Title and Module name
    doc.getBody().appendParagraph(concept).setHeading(DocumentApp.ParagraphHeading.TITLE);
    doc.getBody().appendParagraph(moduleName).setHeading(DocumentApp.ParagraphHeading.SUBTITLE);
    doc.getBody().appendParagraph(''); // Empty line
    
    // COMPILE CONTENT: H + C + D from Module-Resources tab (CORRECT ORDER)
    
    // 1. Slide Specifications (Column H) - FIRST and MOST HEAVILY EMPHASISED
    if (slideSpecifications && String(slideSpecifications).trim()) {
      doc.getBody().appendParagraph('SLIDE SPECIFICATIONS:').setHeading(DocumentApp.ParagraphHeading.HEADING1);
      
      // Parse slide specifications to add clear breaks for LMS AI tool
      const slideContent = String(slideSpecifications).trim();
      const slides = slideContent.split(/(?=SLIDE TITLE:|Slide \d+:|^\d+\.)/gm).filter(s => s.trim());
      
      slides.forEach((slide, index) => {
        if (slide.trim()) {
          // Add clear slide break for LMS AI recognition
          doc.getBody().appendParagraph(`=== SLIDE ${index + 1} ===`).setHeading(DocumentApp.ParagraphHeading.HEADING2);
          doc.getBody().appendParagraph(slide.trim());
          doc.getBody().appendParagraph(''); // Empty line between slides
        }
      });
    }
    
    // 2. Module Description (Column C) - SECOND
    if (moduleDescription && String(moduleDescription).trim()) {
      doc.getBody().appendParagraph('MODULE DESCRIPTION:').setHeading(DocumentApp.ParagraphHeading.HEADING1);
      doc.getBody().appendParagraph(String(moduleDescription).trim());
      doc.getBody().appendParagraph(''); // Empty line
    }
    
    // 3. Key Concepts (Column D) - LAST (for reinforcement of topic ideation)
    if (keyConcepts && String(keyConcepts).trim()) {
      doc.getBody().appendParagraph('KEY CONCEPTS:').setHeading(DocumentApp.ParagraphHeading.HEADING1);
      doc.getBody().appendParagraph(String(keyConcepts).trim());
      doc.getBody().appendParagraph(''); // Empty line
    }
    
    doc.saveAndClose();
    
    // Store LMS document URL back to mapping sheet
    // We can use column I (9) for LMS URLs since it's typically available
    const mappingSheet = SpreadsheetApp.getActiveSheet();
    mappingSheet.getRange(mappingRow, 9).setValue(doc.getUrl());
    
    Logger.log(`Created LMS document: ${docName}`);
    
  } catch (error) {
    Logger.log(`Error creating LMS document: ${error.toString()}`);
  }
}

/**
 * Canonical module audio generator (explicit parameters).
 * - Reads TTS-{concept} for rows where Col A == module
 * - Uses Speaker Notes (Col D) else Script (Col C)
 * - Skips if Audio URL (Col I) exists unless REGENERATE_TTS=true
 * - Writes File Id â†’ Col H, URL â†’ Col I, Notes â†’ Col J
 * - Files WAVs into the module subfolder
 * Returns: { ok:number, skipped:number, errors:number }
 */
function generateModuleAudio_(concept, module, opts) {
  const ui = SpreadsheetApp.getUi();
  const ss = SpreadsheetApp.getActiveSpreadsheet();
  const tts = ss.getSheetByName(`TTS-${concept}`);
  if (!tts) { ui.alert(`TTS sheet for "${concept}" not found.`); return {ok:0,skipped:0,errors:1}; }

  // Get course folder first
  const mappingSheet = SpreadsheetApp.getActiveSpreadsheet().getSheetByName('Mapping');
  const mappingData = mappingSheet.getDataRange().getValues();
  let courseFolderUrl = null;

  for (let i = 1; i < mappingData.length; i++) {
    if (mappingData[i][0] === concept) {
      courseFolderUrl = mappingData[i][19];
      break;
    }
  }

  const courseFolderId = extractIdFromUrl_(courseFolderUrl);
  const courseFolder = DriveApp.getFolderById(courseFolderId);
  const moduleFolder = getModuleSubfolder_(courseFolder, module, 1); // Fixed: provide default moduleIndex
  if (!moduleFolder) { ui.alert(`Could not find or create module subfolder for "${module}".`); return {ok:0,skipped:0,errors:1}; }

  const regen = (opts && typeof opts.regenerate === 'boolean')
    ? opts.regenerate
    : String(PropertiesService.getScriptProperties().getProperty('REGENERATE_TTS') || 'false').toLowerCase() === 'true';

  const last = tts.getLastRow();
  if (last < 2) return {ok:0,skipped:0,errors:0};

  const rows = tts.getRange(2, 1, last - 1, 10).getValues(); // A..J
  let ok=0, skipped=0, errors=0;

  SpreadsheetApp.getActiveSpreadsheet().toast(`Generating audio for "${module}"...`);

  for (let i = 0; i < rows.length; i++) {
    const r = rows[i];
    const mod = String(r[0] || '').trim();                // A: Module
    if (mod !== String(module).trim()) continue;

    const slideNo = r[1];                                  // B: Slide#
    const slideContent = String(r[2] || '').trim();        // C: Slide Content  
    const script  = String(r[3] || '').trim();             // D: Voiceover Scripts (preferred)
    const existingAudio = String(r[6] || '').trim();       // G: Audio (WAV) - CORRECTED COLUMN

    // Use voiceover script if available, otherwise fall back to slide content
    const textToSpeak = script || slideContent;
    
    if (!textToSpeak) continue;

    if (existingAudio && !regen) {
      const prev = String(r[8] || '');                     // I: Notes
      tts.getRange(i + 2, 9).setValue(prev ? `${prev}\nSkipped: audio exists` : 'Skipped: audio exists');
      skipped++;
      continue;
    }

    try {
      const full = `${AUSTRALIAN_PROMPT}\n\n${textToSpeak}`;
      const blob = callGeminiTTS_(full, getVoiceName_(), getSpeechTemperature_());

      const safeModule = String(module).replace(/[\\/:*?"<>|]/g, '_');
      const name = `${concept} â€” ${safeModule} â€” Slide ${slideNo}.wav`;
      const file = moduleFolder.createFile(blob.setName(name));

      // CORRECTED: Audio URL goes in Column G (index 7), Notes in Column I (index 9)  
      tts.getRange(i + 2, 7).setValue(file.getUrl());     // G: Audio (WAV)
      const prev = String(r[8] || '');                     // I: Notes (current)
      const newNote = prev ? `${prev}\nGenerated OK` : 'Generated OK';
      tts.getRange(i + 2, 9).setValue(newNote);           // I: Notes (updated)
      ok++;
      Utilities.sleep(2000); // Increased for Gemini TTS rate limits
    } catch (e) {
      const msg = String(e && e.message || e);
      const prev = String(r[8] || '');                     // I: Notes (current)
      const errorNote = prev ? `${prev}\nERROR: ${msg}` : `ERROR: ${msg}`;
      tts.getRange(i + 2, 9).setValue(errorNote);         // I: Notes (updated)
      errors++;
      Utilities.sleep(3000); // Longer delay after errors
    }
  }

  return { ok, skipped, errors };
}

/**
 * Compatibility wrapper so existing calls keep working.
 * Shown in your screenshot: return generateGeminiAudioForModule(concept, module);
 */
function generateGeminiAudioForModule(concept, module) {
  const res = generateModuleAudio_(concept, module, null);
  SpreadsheetApp.getUi().alert(
    `Module audio complete for "${module}".\nGenerated: ${res.ok}\nSkipped: ${res.skipped}\nErrors: ${res.errors}`
  );
  return res; // preserves the return semantics used in your code
}

function processModificationRequest(){
  const sh = SpreadsheetApp.getActiveSheet();
  if (sh.getName() !== 'Mapping') return SpreadsheetApp.getUi().alert('Run on the Mapping tab.');
  const r = sh.getActiveRange().getRow();
  const concept = sh.getRange(r,1).getValue();
  const request = sh.getRange(r,6).getValue();
  const docUrl = sh.getRange(r,4).getValue();
  if (!request || !docUrl) return SpreadsheetApp.getUi().alert('Need Modification Request (F) and Recommendations link (D).');

  const docId = presIdFromUrl(docUrl) || extractIdFromUrl_(docUrl);
  let currentText = '';
  try{ currentText = DocumentApp.openById(docId).getBody().getText(); }catch(e){}

  const prompt = brandHeader_() + `
Revise the existing course recommendation for "${concept}" based on this request:

${request}

Current document text:
${currentText}

Return the complete revised recommendation with the same headings and refreshed module list.`;
  const revised = callGemini(prompt, 7000);
  const doc = DocumentApp.openById(docId);
  doc.getBody().clear(); doc.getBody().appendParagraph(revised); doc.saveAndClose();

  const names = extractModuleNames_(revised);
  sh.getRange(r,7).setValue(names.join('\n'));
  for (let i=0;i<12;i++){ sh.getRange(r,8+i).setValue(names[i]||''); }
  sh.getRange(r,6).clearContent();
  SpreadsheetApp.getUi().alert('Recommendation updated and module list refreshed.');
}

