  const mappingSheet = ss.getSheetByName('Mapping');
  if (!mappingSheet) {
    ui.alert('Error', 'Mapping sheet not found.', ui.ButtonSet.OK);
    return;
  }

  const mappingData = mappingSheet.getDataRange().getValues();
  let courseFolderUrl = null;

  for (let i = 1; i < mappingData.length; i++) {
    if (mappingData[i][0] === conceptName) {
      courseFolderUrl = mappingData[i][19];
      break;
    }
  }

  if (!courseFolderUrl) {
    ui.alert('Error', 'Course folder URL not found.', ui.ButtonSet.OK);
    return;
  }

  const courseFolderId = extractIdFromUrl_(courseFolderUrl);
  const courseFolder = DriveApp.getFolderById(courseFolderId);

  const moduleFolder = getModuleSubfolder_(courseFolder, moduleName, 1);
  if (!moduleFolder) return;

  const last = tts.getLastRow();
  if (last < 2) return ui.alert('No TTS rows found.');

  const data = tts.getRange(2, 1, last - 1, 10).getValues(); // A..J
  let ok = 0, skipped = 0, errors = 0;

  console.log(`Starting audio generation for module: ${moduleName}`);
  console.log(`Processing ${data.length} rows in TTS sheet`);
  SpreadsheetApp.getActiveSpreadsheet().toast(`Generating audio for "${moduleName}"â€¦ Processing ${data.length} slides.`);

  for (let i = 0; i < data.length; i++) {
    const row = data[i];
    const mod = String(row[0] || '').trim();          // Col A
    if (mod !== String(moduleName).trim()) continue;

    const slideNo = row[1];                            // Col B - Slide number
    const slideContent = String(row[2] || '').trim();  // Col C - Slide Content
    const script  = String(row[3] || '').trim();       // Col D - Voiceover Scripts (preferred)
    const existingAudio = String(row[6] || '').trim(); // Col G - Audio (WAV) files

    // Use voiceover script if available, otherwise fall back to slide content
    const textToSpeak = script || slideContent;
    
    if (!textToSpeak) {
      console.log(`Skipping slide ${slideNo} - no content to convert to audio`);
      continue;
    }
    
    // FIXED: Only skip if there's a real audio URL (not dummy data)
    if (existingAudio && existingAudio.startsWith('http') && existingAudio.includes('drive.google.com')) {
      console.log(`Skipping slide ${slideNo} - audio already exists: ${existingAudio}`);
      skipped++;
      continue;
    } else if (existingAudio && !existingAudio.startsWith('http')) {
      // Clear dummy data like "Default Voice" 
      console.log(`Clearing dummy data from Column G: "${existingAudio}"`);
      tts.getRange(i + 2, 7).setValue(''); // Clear Column G
    }

    try {
      console.log(`Generating audio for slide ${slideNo}...`);
      const fullText = `${AUSTRALIAN_PROMPT}\n\n${textToSpeak}`;
      const blob = callGeminiTTS_(fullText, getVoiceName_(), getSpeechTemperature_());
      const safeMod = String(moduleName).replace(/[\\/:*?"<>|]/g, '_');
      const fname = `${conceptName} â€” ${safeMod} â€” Slide ${slideNo}.wav`;
      const file = moduleFolder.createFile(blob.setName(fname));
      const url = file.getUrl();

      // Update Column G with the audio file URL
      tts.getRange(i + 2, 7).setValue(url); // Col G - Audio (WAV)
      
      // Update notes in Column I
      const prev = String(row[8] || ''); // Col I - Notes
      const newNote = prev ? (prev + '\nAudio generated successfully') : 'Audio generated successfully';
      tts.getRange(i + 2, 9).setValue(newNote);
      ok++;
      console.log(`Successfully generated audio for slide ${slideNo}`);
      Utilities.sleep(2000); // Increased pacing for Gemini TTS rate limits
    } catch (e) {
      console.error(`Error generating audio for slide ${slideNo}: ${e.message}`);
      const prev = String(row[8] || ''); // Col I - Notes
      const errorNote = prev ? (prev + `\nERROR: ${e.message}`) : `ERROR: ${e.message}`;
      tts.getRange(i + 2, 9).setValue(errorNote);
      errors++;
    }
  }

  // Provide comprehensive completion report
  console.log(`Audio generation complete for ${moduleName}. Generated: ${ok}, Skipped: ${skipped}, Errors: ${errors}`);
  
  let message = `Audio Generation Complete for "${moduleName}"\n\n`;
  message += `âœ… Successfully Generated: ${ok} audio file(s)\n`;
  message += `â© Skipped (already exists): ${skipped} file(s)\n`;
  message += `âŒ Errors: ${errors} file(s)\n\n`;
  
  if (ok > 0) {
    message += `Audio files have been saved to the "${moduleName}" folder in your course directory.\n`;
  }
  
  if (errors > 0) {
    message += `Please check the Notes column (Column I) in the TTS sheet for detailed error information.\n`;
  }
  
  message += `\nAll generated audio files are now available in Column G (Audio WAV) of the TTS sheet.`;

  ui.alert('Audio Generation Report', message, ui.ButtonSet.OK);
}

function cleanModuleAudioFiles() {
  const ss = SpreadsheetApp.getActiveSpreadsheet();
  const activeSheet = ss.getActiveSheet();
  const sheetName = activeSheet.getName();
  
  // Check if we're on a TTS sheet
  if (!sheetName.startsWith('TTS-')) {
    SpreadsheetApp.getUi().alert('Wrong Sheet', 'Please navigate to a TTS sheet first to select the module for audio cleanup.', SpreadsheetApp.getUi().ButtonSet.OK);
    return;
  }
  
  const conceptName = sheetName.replace('TTS-', '');
  
  // Get module name from selected row in TTS sheet
  const activeRow = activeSheet.getActiveRange().getRow();
  if (activeRow < 2) {
    SpreadsheetApp.getUi().alert('Row Selection', 'Please select a row with module data (not the header).', SpreadsheetApp.getUi().ButtonSet.OK);
    return;
  }
  
  const moduleName = activeSheet.getRange(activeRow, 1).getValue();
  if (!moduleName) {
    SpreadsheetApp.getUi().alert('Module Missing', 'No module name found in selected row.', SpreadsheetApp.getUi().ButtonSet.OK);
    return;
  }
  
  const ui = SpreadsheetApp.getUi();
  const response = ui.alert('Confirm Deletion', `This will trash all .wav audio files for the module "${moduleName}". Are you sure?`, ui.ButtonSet.YES_NO);
  if (response !== ui.Button.YES) return;

  // Get course folder first  
  const mappingSheet = ss.getSheetByName('Mapping');
  const mappingData = mappingSheet.getDataRange().getValues();
  let courseFolderUrl = null;

  for (let i = 1; i < mappingData.length; i++) {
    if (mappingData[i][0] === conceptName) {
      courseFolderUrl = mappingData[i][19];
      break;
    }
  }

  const courseFolderId = extractIdFromUrl_(courseFolderUrl);
  const courseFolder = DriveApp.getFolderById(courseFolderId);
  const targetFolder = getModuleSubfolder_(courseFolder, moduleName, 1);

  if (!targetFolder) return;

  const files = targetFolder.getFilesByType(MimeType.WAV);
  let count = 0;
  while (files.hasNext()) {
    files.next().setTrashed(true);
    count++;
  }
  
  const ttsSheet = ss.getSheetByName(`TTS-${conceptName}`);
  if (ttsSheet) {
    const data = ttsSheet.getRange("A2:I").getValues(); // Extended to column I for audio URLs
    for (let i = 0; i < data.length; i++) {
      if (String(data[i][0]).trim().toLowerCase() === moduleName.toLowerCase()) {
        ttsSheet.getRange(i + 2, 8).clearContent(); // clear File ID col H
        ttsSheet.getRange(i + 2, 9).clearContent(); // clear URL col I
      }
    }
  }
  
  ui.alert(`Trashed ${count} audio files for "${moduleName}" and cleared links from the sheet.`);
}

function archiveCourse() {
  const ui = SpreadsheetApp.getUi();
  const ss = SpreadsheetApp.getActiveSpreadsheet();
  const mappingSheet = ss.getSheetByName('Mapping');
  
  if (!mappingSheet) return ui.alert('Mapping sheet not found.');
  
  const row = mappingSheet.getActiveCell().getRow();
  if (row < 2) return ui.alert('Select a course row to archive.');
  
  const courseName = mappingSheet.getRange(row, 1).getValue();
  const courseFolderUrl = mappingSheet.getRange(row, 20).getValue(); // Column T
  if (!courseName || !courseFolderUrl) return ui.alert('Cannot archive: course name or folder not found.');

  const response = ui.alert(
    'Confirm Complete Archival', 
    `This will:\n\n` +
    `â€¢ Move content tabs for "${courseName}" into a backup spreadsheet\n` +
    `â€¢ Remove the course row from the Mapping tab\n` +
    `â€¢ Clean up all related sheets (Module-Resources, TTS, etc.)\n` +
    `â€¢ Preserve System Status sheet if it contains other course data\n\n` +
    `This action cannot be undone. Proceed?`, 
    ui.ButtonSet.YES_NO
  );
  
  if (response !== ui.Button.YES) return;

  try {
    const courseFolderId = extractIdFromUrl_(courseFolderUrl);
    const courseFolder = DriveApp.getFolderById(courseFolderId);

    // Create backup spreadsheet in course folder
    const backupSpreadsheet = SpreadsheetApp.create(`${courseName} - Course Content Backup`);
    const backupFile = DriveApp.getFileById(backupSpreadsheet.getId());
    courseFolder.addFile(backupFile);
    DriveApp.getRootFolder().removeFile(backupFile);

    // Identify all sheets related to this course
    const allSheets = ss.getSheets();
    const sheetsToBackup = [];
    const sheetsToDelete = [];
    
    allSheets.forEach(sheet => {
      const sheetName = sheet.getName();
      
      // Course-specific sheets to backup and delete
      if (sheetName === `Module-Resources-${courseName}` || 
          sheetName === `TTS-${courseName}`) {
        sheetsToBackup.push(sheet);
        sheetsToDelete.push(sheet);
      }
    });
    
    // Backup identified sheets
    sheetsToBackup.forEach(sheet => {
      try {
        sheet.copyTo(backupSpreadsheet);
        Logger.log(`Backed up sheet: ${sheet.getName()}`);
      } catch (error) {
        Logger.log(`Error backing up sheet ${sheet.getName()}: ${error.toString()}`);
      }
    });
    
    // Remove default placeholder sheet from backup
    const placeholderSheet = backupSpreadsheet.getSheetByName('Sheet1');
    if (placeholderSheet) {
      try {
        backupSpreadsheet.deleteSheet(placeholderSheet);
      } catch (error) {
        Logger.log(`Could not delete placeholder sheet: ${error.toString()}`);
      }
    }

    // Delete course-specific sheets from main spreadsheet
    sheetsToDelete.forEach(sheet => {
      try {
        ss.deleteSheet(sheet);
        Logger.log(`Deleted sheet: ${sheet.getName()}`);
      } catch (error) {
        Logger.log(`Error deleting sheet ${sheet.getName()}: ${error.toString()}`);
        ui.alert(`Warning: Could not delete sheet "${sheet.getName()}": ${error.message}`);
      }
    });

    // Remove course row from Mapping sheet
    mappingSheet.deleteRow(row);
    
    // Check if Mapping sheet is now empty (only headers remain)
    const mappingData = mappingSheet.getDataRange().getValues();
    const hasOtherCourses = mappingData.length > 1; // More than just header row
    
    // Check System Status sheet for other courses
    const systemStatusSheet = ss.getSheetByName('System Status');
    let hasOtherSystemData = false;
    
    if (systemStatusSheet) {
      const systemData = systemStatusSheet.getDataRange().getValues();
      // Check if there are entries for other courses (skip header row)
      hasOtherSystemData = systemData.slice(1).some(row => 
        row[0] && row[0].toString().trim() !== '' && row[0].toString().trim() !== courseName
      );
    }
    
    // Clean up main sheets if no other courses exist
    if (!hasOtherCourses) {
      const response2 = ui.alert(
        'Complete Cleanup', 
        `This was the last course in the system. Would you like to also remove the Mapping and System Status sheets to completely clean the workspace?\n\n` +
        `Note: This will leave you with just the "How to Use" tab and you'll need to run Step 1 again for new courses.`,
        ui.ButtonSet.YES_NO
      );
      
      if (response2 === ui.Button.YES) {
        // Delete Mapping sheet
        if (mappingSheet) {
          try {
            ss.deleteSheet(mappingSheet);
            Logger.log('Deleted Mapping sheet');
          } catch (error) {
            Logger.log(`Could not delete Mapping sheet: ${error.toString()}`);
          }
        }
        
        // Delete System Status sheet if it has no other data
        if (systemStatusSheet && !hasOtherSystemData) {
          try {
            ss.deleteSheet(systemStatusSheet);
            Logger.log('Deleted System Status sheet');
          } catch (error) {
            Logger.log(`Could not delete System Status sheet: ${error.toString()}`);
          }
        }
        
        ui.alert(
          'Complete Archival Finished', 
          `âœ… Course "${courseName}" has been completely archived.\n\n` +
          `ðŸ“ Backup created in course folder\n` +
          `ðŸ§¹ All related sheets removed\n` +
          `ðŸ  Workspace cleaned for new projects\n\n` +
          `To start a new course, use "Step 1: Setup & Add First Course" from the menu.`,
          ui.ButtonSet.OK
        );
      } else {
        ui.alert(
          'Archival Complete', 
          `âœ… Course "${courseName}" has been archived and backed up.\n\n` +
          `The Mapping and System Status sheets have been preserved for future use.`,
          ui.ButtonSet.OK
        );
      }
    } else {
      ui.alert(
        'Archival Complete', 
        `âœ… Course "${courseName}" has been archived and backed up.\n\n` +
        `Other courses remain in the system, so the Mapping and System Status sheets have been preserved.`,
        ui.ButtonSet.OK
      );
    }
    
  } catch (error) {
    Logger.log(`Archival failed: ${error.toString()}`);
    ui.alert(`Archival failed: ${error.message}\n\nCheck the script logs for more details.`);
  }
}

// ===== Sophisticated script generation (unchanged logic, AU context retained) =====

function populateTTSWithAIGeneratedVoiceovers(){
  const mres = SpreadsheetApp.getActiveSheet();
  if (!mres.getName().startsWith('Module-Resources-')) {
    SpreadsheetApp.getUi().alert('Run this on a Module-Resources-{Concept} tab (select a module row).');
    return;
  }
  const concept = mres.getName().replace(/^Module-Resources-/, '');
  const row = mres.getActiveRange().getRow();
  const moduleName = mres.getRange(row, 1).getValue();
  const norm = s => String(s||'').replace(/\s+/g,' ').trim();

  if (!norm(moduleName)) { 
    SpreadsheetApp.getUi().alert('No Module Name in Column A for the selected row.'); 
    return; 
  }

  const tts = ensureTTSSheet(concept);
  const last = tts.getLastRow();
  const slideContents = [];
  const rows = [];
  
  for (let r=2; r<=last; r++){
    if (norm(tts.getRange(r,1).getValue()) === norm(moduleName)) {
      const slideContent = tts.getRange(r, 3).getValue(); // Column C: Slide Content
      const slideNumber = Number(tts.getRange(r,2).getValue()) || r;
      const slideTitle = String(slideContent || '').split('\n')[0].replace(/^#+\s*/, '').trim();
      
      if (slideContent && String(slideContent).trim()) {
        slideContents.push({
          title: slideTitle,
          content: String(slideContent).trim(),
          slideNumber: slideNumber
        });
        rows.push({ r, n: slideNumber });
      }
    }
  }
  
  if (!slideContents.length) { 
    SpreadsheetApp.getUi().alert('No slide content found in TTS tab Column C for this module. Run "Generate Full Module Resource Suite" first.'); 
    return; 
  }
  
  slideContents.sort((a,b) => a.slideNumber - b.slideNumber);
  rows.sort((a,b) => a.n - b.n);

  const prompts = slideContents.map((slide, i) => {
    const lines = slide.content.split('\n').filter(line => line.trim());
    const title = lines[0].replace(/^#+\s*/, '').trim();
    const bodyLines = lines.slice(1).filter(line => 
      line.trim() && 
      !line.match(/^#+\s/) &&
      !line.match(/^\s*[-*â€¢]\s*$/)
    );
    
    const bodyContent = bodyLines
      .map(line => line.replace(/^\s*[-*â€¢]\s*/, '').trim())
      .filter(line => line.length > 0)
      .join('. ');
    
    return brandHeader_() + `
Create a sophisticated voiceover script for an executive-level healthcare education presentation. This is slide ${i+1} of ${slideContents.length} in the "${concept}" professional development module.

SLIDE TITLE: ${title}
SLIDE CONTENT TO ENHANCE: ${bodyContent}

[... requirements unchanged for brevity in this paste, keep your original block here verbatim ...]`;
  });

  if (!prompts.length){ 
    SpreadsheetApp.getUi().alert('No prompts built for this module.'); 
    return; 
  }

  const scripts = [];
  const durations = [];
  
  trackProgress('Enhanced Voiceover Generation', 0, prompts.length, 'Generating executive-level voiceover scripts...');
  
  for (let i=0; i<prompts.length; i++){
    try{
      trackProgress('Enhanced Voiceover Generation', i + 1, prompts.length, `Generating sophisticated script for slide ${i + 1}...`);
      const script = au(callGeminiWithRetry(prompts[i], 1500));
      const trimmed = String(script||'').trim();
      if (!trimmed) throw new Error('Empty response from model');
      scripts.push([trimmed]);
      const words = trimmed.split(/\s+/).filter(Boolean).length;
      const mins = Math.max(0.5, Math.round((words / 160) * 10) / 10);
      durations.push([`${mins} min`]);
    }catch(e){
      scripts.push([`[[ERROR: ${e.message}]]`]);
      durations.push(['']);
    }
    Utilities.sleep(8000);
  }

  if (rows.length < scripts.length){
    SpreadsheetApp.getUi().alert(
      `Parsed ${scripts.length} slides but found only ${rows.length} TTS rows for "${moduleName}". Re-run "Resync: Seed TTS rowsâ€¦" and try again.`
    );
    return;
  }
  const start = rows[0].r;
  tts.getRange(start, 4, scripts.length, 1).setValues(scripts);   // Col D: speaker notes
  tts.getRange(start, 5, durations.length, 1).setValues(durations); // Col E

  const wrote = scripts.filter(x => String(x[0]||'').trim() && !/^\[\[ERROR:/.test(x[0])).length;
  if (!wrote){
    SpreadsheetApp.getUi().alert('No voiceover text was generated. Check slide content in TTS tab Column C and try again.');
    return;
  }
  SpreadsheetApp.getUi().alert(`Generated ${wrote} executive-level voiceover script(s) for "${moduleName}" using enhanced slide content from TTS tab.`);
}

function generateImagePromptsForTab(){
  const sh = SpreadsheetApp.getActiveSheet();
  if (!sh.getName().startsWith('Module-Resources-')) return SpreadsheetApp.getUi().alert('Run on Module-Resources-{Concept}.');
  const concept = sh.getName().replace('Module-Resources-','');
  let img = SpreadsheetApp.getActive().getSheetByName(`IMG-${concept}`);
  if (!img){ img=SpreadsheetApp.getActive().insertSheet(`IMG-${concept}`); img.getRange(1,1,1,4).setValues([[ 'Module Name','Slide #','Prompt (AU context)','Drive File ID (optional)' ]]).setFontWeight('bold'); }
  const last = sh.getLastRow();
  for (let r=2; r<=last; r++){
    const module = sh.getRange(r,1).getValue();
    const spec = sh.getRange(r,8).getValue();
    if (!module || !spec) continue;
    const slides = parseSlideSpecs(spec);
    slides.forEach(function(s,i){
      const phrases = s.body.map(function(l){ return String(l).replace(/^\s*[-*â€¢]\s*/,'').trim(); }).filter(Boolean).slice(0,6).join('; ');
      const p = `AU healthcare visual for "${s.title}". Emphasise clinical context; accessible contrast; no baked-in text; culturally safe. Ideas: ${phrases}.`;
      img.appendRow([module, i+1, p, '']);
    });
  }
  SpreadsheetApp.getUi().alert('Image prompts written.');
}

