/**
 * CRITICAL: Fix existing TTS sheets with wrong headers
 * Call this function to update any TTS sheets created with old headers
 */
function fixTTSSheetHeaders() {
  const ss = SpreadsheetApp.getActiveSpreadsheet();
  const ui = SpreadsheetApp.getUi();
  
  // Find all TTS sheets
  const sheets = ss.getSheets();
  const ttsSheets = sheets.filter(sheet => sheet.getName().startsWith('TTS-'));
  
  if (ttsSheets.length === 0) {
    ui.alert('No TTS sheets found to fix.');
    return;
  }
  
  const correctHeaders = [
    'Module Name',           // A - Module identifier
    'Slide#',                // B - Slide sequence number
    'Slide Content',         // C - Extended sophisticated content for voiceover development
    'Voiceover Scripts',     // D - Speaker notes (preferred name)
    'Duration',              // E - Estimated voiceover length
    'Slides (PPT-ready)',    // F - Google Slides file link (row 1 after menu #6)
    'Audio (WAV)',          // G - TTS files for each slide (after menu #8)
    'Image Prompt',         // H - Generated for each slide during creation
    'Notes',                // I - Filler column (due to col J dependencies)
    'Alternate Slide Content' // J - Critical for slide generation workflow
  ];
  
  let fixedCount = 0;
  
  ttsSheets.forEach(sheet => {
    try {
      // Check if headers need fixing (look for old header names)
      const currentHeaders = sheet.getRange(1, 1, 1, 10).getValues()[0];
      const needsFix = currentHeaders.includes('Slide Title') || 
                      currentHeaders.includes('Narration Script') || 
                      currentHeaders.includes('Audio File URL') ||
                      currentHeaders.includes('Voice Settings');
      
      if (needsFix) {
        // Backup existing data if any
        const lastRow = sheet.getLastRow();
        let existingData = [];
        if (lastRow > 1) {
          existingData = sheet.getRange(2, 1, lastRow - 1, Math.min(sheet.getLastColumn(), 10)).getValues();
        }
        
        // Update headers
        sheet.getRange(1, 1, 1, 10).setValues([correctHeaders]);
        
        // Apply header formatting
        const headerRange = sheet.getRange(1, 1, 1, 10);
        headerRange.setFontWeight('bold')
                  .setBackground('#4285f4')
                  .setFontColor('#ffffff')
                  .setBorder(true, true, true, true, true, true);
        
        // Set correct column widths
        sheet.setColumnWidth(1, 150);  // A - Module Name
        sheet.setColumnWidth(2, 80);   // B - Slide#
        sheet.setColumnWidth(3, 400);  // C - Slide Content
        sheet.setColumnWidth(4, 300);  // D - Voiceover Scripts
        sheet.setColumnWidth(5, 100);  // E - Duration
        sheet.setColumnWidth(6, 150);  // F - Slides (PPT-ready)
        sheet.setColumnWidth(7, 150);  // G - Audio (WAV)
        sheet.setColumnWidth(8, 250);  // H - Image Prompt
        sheet.setColumnWidth(9, 120);  // I - Notes
        sheet.setColumnWidth(10, 350); // J - Alternate Slide Content
        
        // Restore data if it exists (map to new structure as needed)
        if (existingData.length > 0) {
          // Pad rows to ensure 10 columns
          const paddedData = existingData.map(row => {
            const newRow = Array(10).fill('');
            for (let i = 0; i < Math.min(row.length, 10); i++) {
              newRow[i] = row[i] || '';
            }
            return newRow;
          });
          
          sheet.getRange(2, 1, paddedData.length, 10).setValues(paddedData);
        }
        
        fixedCount++;
        console.log(`Fixed TTS sheet: ${sheet.getName()}`);
      }
    } catch (error) {
      console.error(`Error fixing TTS sheet ${sheet.getName()}: ${error.message}`);
    }
  });
  
  if (fixedCount > 0) {
    ui.alert('TTS Headers Fixed', `Successfully updated headers for ${fixedCount} TTS sheet(s).\n\nThe sheets now use the correct column structure:\n- Column C: Slide Content\n- Column D: Voiceover Scripts\n- Column G: Audio (WAV)\n- Column I: Notes`, ui.ButtonSet.OK);
  } else {
    ui.alert('All TTS sheets already have correct headers.');
  }
}

/**
