 */
function cleanTTSDummyData() {
  const ss = SpreadsheetApp.getActiveSpreadsheet();
  const ui = SpreadsheetApp.getUi();
  
  // Find all TTS sheets
  const sheets = ss.getSheets();
  const ttsSheets = sheets.filter(sheet => sheet.getName().startsWith('TTS-'));
  
  if (ttsSheets.length === 0) {
    ui.alert('No TTS sheets found to clean.');
    return;
  }
  
  let cleanedCount = 0;
  let totalCleared = 0;
  
  ttsSheets.forEach(sheet => {
    try {
      const lastRow = sheet.getLastRow();
      if (lastRow < 2) return;
      
      // Get data from columns G (Audio) and H (Image Prompt)
      const audioData = sheet.getRange(2, 7, lastRow - 1, 1).getValues();
      const imageData = sheet.getRange(2, 8, lastRow - 1, 1).getValues();
      
      let cleared = false;
      
      // Clean Column G (Audio) - remove non-URL dummy data
      for (let i = 0; i < audioData.length; i++) {
