        if (value && !value.startsWith('http')) {
          // Clear dummy data like "Default Voice"
          sheet.getRange(i + 2, 7).setValue('');
          console.log(`Cleared dummy audio data: "${value}"`);
          cleared = true;
          totalCleared++;
        }
      }
      
      // Clean Column H (Image Prompt) - remove non-prompt dummy data  
      for (let i = 0; i < imageData.length; i++) {
        const value = String(imageData[i][0] || '').trim();
        if (value && (value === 'Script Ready' || value === 'Voice Settings' || value.length < 20)) {
          // Clear dummy data like "Script Ready" or very short non-descriptive text
          sheet.getRange(i + 2, 8).setValue('');
          console.log(`Cleared dummy image data: "${value}"`);
          cleared = true;
          totalCleared++;
