  if (totalCleared > 0) {
    ui.alert('TTS Dummy Data Cleaned', `Successfully cleaned ${totalCleared} dummy data entries from ${cleanedCount} TTS sheet(s).\n\nRemoved dummy data like:\n- "Default Voice" from Column G\n- "Script Ready" from Column H\n\nAudio generation should now work properly!`, ui.ButtonSet.OK);
  } else {
    ui.alert('No dummy data found to clean.');
  }
}

