parameters.jsCode:
// Get slide data from calling workflow
const slideData = $json;

// Extract all fields
return [{
  json: {
    course_id: slideData.course_id,
    module_number: slideData.module_number,
    slide_number: slideData.slide_number,
    module_title: slideData.module_title || 'Module',
    course_title: slideData.course_title || 'Course',
    slide_title: slideData.slide_title || 'Slide',
    voiceover_script: slideData.voiceover_script || '',
    image_prompt: slideData.image_prompt || '',
    content_points: slideData.content_points || '',
    target_audience: slideData.target_audience || 'Healthcare Professionals',
    timestamp: slideData.timestamp || new Date().toISOString()
  }
}];

// Decide whether to enhance script or use Gemini's script as-is
const geminiScript = $json.voiceover_script || '';

// If Gemini already provided a good script (300+ words), use it
// Otherwise, we'll enhance it with OpenAI
const useGeminiScript = geminiScript.length > 300;

return [{
  json: {
    ...$json,
    use_gemini_script: useGeminiScript,
    needs_enhancement: !useGeminiScript
  }
}];

parameters.text:
=Transform the following slide content into flowing, engaging paragraphs for professional Australian healthcare practitioners:

Requirements:
1. Transform the outline into flowing, engaging paragraphs (NO bullet points)
2. Use sophisticated, professional language suitable for {{ $json.target_audience }}
3. Include practical applications and real-world examples
4. Reference Australian healthcare context naturally (AHPRA, NMBA, PBS, MBS where relevant)
5. Aim for 300-450 words for optimal voiceover timing (2-3 minutes)
6. Create content that demonstrates authority and expertise
7. Use evidence-based language and cite relevant Australian guidelines
8. Include transition phrases for smooth audio flow

Slide Title: {{ $json.slide_title }}
Slide Content: {{ $json.voiceover_script }}
Module: {{ $json.module_title }}
Course: {{ $json.course_title }}
Audience: {{ $json.target_audience }}

Generate professional voiceover script as flowing paragraphs:

parameters.jsCode:
// Extract enhanced script from OpenAI response
let enhancedScript = '';

if ($json.message && $json.message.content) {
  enhancedScript = $json.message.content;
} else if ($json.text) {
  enhancedScript = $json.text;
} else if (typeof $json === 'string') {
  enhancedScript = $json;
} else {
  // Fallback to original script
  const originalData = $('2. Check Script Quality').item.json;
  enhancedScript = originalData.voiceover_script;
}

// Clean the script for TTS
const cleanScript = enhancedScript
  .replace(/\*\*.*?\*\*/g, '')  // Remove bold markdown
  .replace(/\[.*?\]/g, ' ')     // Remove square brackets
  .replace(/\n\n+/g, ' ')        // Replace multiple newlines with space
  .replace(/\s+/g, ' ')         // Replace multiple spaces with single space
  .trim();

// Get original data
const originalData = $('2. Check Script Quality').item.json;

return [{
  json: {
    course_id: originalData.course_id,
    module_number: originalData.module_number,
    slide_number: originalData.slide_number,
    slide_title: originalData.slide_title,
    voiceScript: cleanScript,
    slideName: `slide_${originalData.module_number}_${originalData.slide_number}`,
    module_title: originalData.module_title,
    course_title: originalData.course_title,
    target_audience: originalData.target_audience,
    timestamp: originalData.timestamp
  }
}];

// Use Gemini script as-is (already good quality)
const originalData = $json;

// Clean the script for TTS
const cleanScript = originalData.voiceover_script
  .replace(/\*\*.*?\*\*/g, '')  // Remove bold markdown
  .replace(/\[.*?\]/g, ' ')     // Remove square brackets
  .replace(/\n\n+/g, ' ')        // Replace multiple newlines with space
  .replace(/\s+/g, ' ')         // Replace multiple spaces with single space
  .trim();

return [{
  json: {
    course_id: originalData.course_id,
    module_number: originalData.module_number,
    slide_number: originalData.slide_number,
    slide_title: originalData.slide_title,
    voiceScript: cleanScript,
    slideName: `slide_${originalData.module_number}_${originalData.slide_number}`,
    module_title: originalData.module_title,
    course_title: originalData.course_title,
    target_audience: originalData.target_audience,
    timestamp: originalData.timestamp
  }
}];

// Parse TTS response
let audioUrl = '';
let success = false;

if ($json.audioUrl) {
  audioUrl = $json.audioUrl;
  success = true;
} else if ($json.url) {
  audioUrl = $json.url;
  success = true;
} else if ($json.success === false) {
  success = false;
  audioUrl = 'Generation failed';
} else {
  // Try to extract from response
  try {
    const response = typeof $json === 'string' ? JSON.parse($json) : $json;
    audioUrl = response.audioUrl || response.url || 'Unknown';
    success = !!audioUrl && audioUrl !== 'Unknown';
  } catch (e) {
    audioUrl = 'Parse error';
    success = false;
  }
}

const scriptData = $('5. Merge Script Paths').item.json;

return [{
  json: {
    course_id: scriptData.course_id,
    module_number: scriptData.module_number,
    slide_number: scriptData.slide_number,
    slide_title: scriptData.slide_title,
    module_title: scriptData.module_title,
    course_title: scriptData.course_title,
    voiceScript: scriptData.voiceScript,
    audioUrl: audioUrl,
    success: success,
    slideName: scriptData.slideName,
    target_audience: scriptData.target_audience,
    timestamp: scriptData.timestamp
  }
}];
